import{_ as a,c as t,a1 as r,o as l}from"./chunks/framework.BDxlaA_J.js";const i="/assets/mppt-1210-hus.fQmaa54Q.jpg",m=JSON.parse('{"title":"MPPT 1210 HUS - Battery & USB Charger","description":"","frontmatter":{},"headers":[],"relativePath":"hardware/mppt-1210-hus.md","filePath":"hardware/mppt-1210-hus.md","lastUpdated":1752654879000}'),o={name:"hardware/mppt-1210-hus.md"};function s(p,e,n,c,h,u){return l(),t("div",null,e[0]||(e[0]=[r('<h1 id="mppt-1210-hus-battery-usb-charger" tabindex="-1">MPPT 1210 HUS - Battery &amp; USB Charger <a class="header-anchor" href="#mppt-1210-hus-battery-usb-charger" aria-label="Permalink to &quot;MPPT 1210 HUS - Battery &amp; USB Charger&quot;">​</a></h1><p><img src="https://img.shields.io/badge/development%20stage-eval-yellow.svg" alt="Development Stage"> Tested prototype, evaluation boards available.</p><p>This charge controller is based on the <a href="/hardware/cc-inactive.html#mppt-2420-lc">20A MPPT</a>, but has reduced number of components and cheaper DC/DC power stage for 10A.</p><p>The power is sufficient for small 12V applications with up to 150 W solar panels.</p><p>With the integrated dual USB output you can directly charge your mobile phone or power other devices such as Raspberry Pi.</p><p><img src="'+i+'" alt="Charge controller PCB"></p><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h2><ul><li>Maximum Power Point Tracking (MPPT)</li><li>12V nominal battery voltage</li><li>10A max. charging current</li><li>40V max. solar input (36 to 48-cell panels, ideally so-called 12V panels with 36 cells)</li><li>Ultra low-power 32bit ARM MCU (STM32L072)</li><li>Dual USB charging ports</li><li>Expandable via Olimex Universal Extension Connector (UEXT) featuring I2C, Serial and SPI interface (e.g. used for display, WIFI communication, etc.)</li><li>Built-in protection: <ul><li>Overvoltage</li><li>Undervoltage</li><li>Overcurrent</li><li>PV short circuit</li><li>PV reverse polarity</li><li>Battery reverse polarity (destructive, fuse is blown)</li></ul></li></ul><h2 id="development" tabindex="-1">Development <a class="header-anchor" href="#development" aria-label="Permalink to &quot;Development&quot;">​</a></h2><p>The charge controller is under active development and you are welcome to participate.</p><p>Visit the <a href="https://github.com/LibreSolar/MPPT-1210-HUS" title="10A MPPT Solar Charge Controller with USB output" target="_blank" rel="noreferrer">GitHub page</a> for schematics and board layout.</p>',11)]))}const g=a(o,[["render",s]]);export{m as __pageData,g as default};
