import{_ as t,c as a,a1 as r,o as l}from"./chunks/framework.BDxlaA_J.js";const i="/assets/pwm-2420-lus.Ddqky8z8.png",m=JSON.parse('{"title":"PWM 2420 LUS - Battery & USB Charger","description":"","frontmatter":{},"headers":[],"relativePath":"hardware/pwm-2420-lus.md","filePath":"hardware/pwm-2420-lus.md","lastUpdated":1752654879000}'),o={name:"hardware/pwm-2420-lus.md"};function s(n,e,c,p,h,d){return l(),a("div",null,e[0]||(e[0]=[r('<h1 id="pwm-2420-lus-battery-usb-charger" tabindex="-1">PWM 2420 LUS - Battery &amp; USB Charger <a class="header-anchor" href="#pwm-2420-lus-battery-usb-charger" aria-label="Permalink to &quot;PWM 2420 LUS - Battery &amp; USB Charger&quot;">​</a></h1><p><img src="https://img.shields.io/badge/development%20stage-eval-yellow.svg" alt="Development Stage"> Tested prototype, only minor issues left.</p><p>This PWM charge controller is cheaper than the MPPT charge controllers, as it does not contain the internal powerful DC/DC converter necessary for MPPT. The lower production cost and easy manufacturing with larger SMD components makes it ideally suited for solar home systems (SHS) for rural electrification.</p><p>The design allows to use either SMD MOSFETs or TO220 THT MOSFETs, which can be easily attached to a heat sink.</p><p><img src="'+i+'" alt="Charge controller PCB"></p><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h2><ul><li>12V/24V battery voltage</li><li>20A max. charge current (10A with cheaper MOSFETs possible)</li><li>55V max. solar input</li><li>32bit ARM MCU (STM32L072)</li><li>Expandable via Olimex Universal Extension Connector (UEXT) featuring I2C, Serial and SPI interface (e.g. used for display, WIFI communication, etc.)</li><li>USB charging (single port)</li><li>Low-side load switching</li><li>Built-in protection (yet to be tested): <ul><li>Overvoltage</li><li>Undervoltage</li><li>Overcurrent</li><li>PV short circuit</li><li>PV reverse polarity (for max. module open circuit voltage of around 40V)</li><li>Battery reverse polarity</li></ul></li></ul><h2 id="development" tabindex="-1">Development <a class="header-anchor" href="#development" aria-label="Permalink to &quot;Development&quot;">​</a></h2><p>The charge controller is under active development and you are welcome to participate.</p><p>Visit the <a href="https://github.com/LibreSolar/PWM-2420-LUS" title="20A PWM Solar Charge Controller with USB output" target="_blank" rel="noreferrer">GitHub page</a> for schematics, board layout and firmware.</p>',10)]))}const g=t(o,[["render",s]]);export{m as __pageData,g as default};
